import React from 'react';
import starIcon from '../images/star.png';
// import thumbnail from '../images/image 12.png'
export default function Card ({image, rate}) {
    return (
        <div className="card">
            <img src={image} alt='thumbnail' className='thumbnail'/>

            <div className='card-status'>
                <img src={starIcon} alt='star-icon' className='star'/>
                <span>{rate}</span>
                <span className='grey'>({props.votes}) &#x2022;</span>
                <span className='grey'>{props.country}</span>
            </div>
            <p>{props.description}</p>
            <p><span className='bold'>From {props.price}</span> / person</p>
        </div>
    );
}
